mapper.select:
SELECT * from Shares





                and  Shares.CreateTime 



        ORDER BY ${sidx} ${sord}

 limit #{recordStart},#{recordEnd};

SELECT count(*) from Shares





                and  Shares.createTime 


;

SELECT * FROM (
SELECT count(*) as buyNum,sharesName,sharesCode , sum(ratio) as totalRatio FROM shares
LEFT JOIN fund ON shares.fundId=fund.id

         AND   oneMonth>=#{oneMonth}
         AND threeMonth>=#{threeMonth}
         AND sixMonth>=#{sixMonth}
         AND oneYear>=#{oneYear}
         AND threeYear>=#{threeYear}
         AND always>=#{always}

GROUP BY shares.sharesName ,sharesCode

        ORDER BY ${sidx} ${sord}

)
 as temp limit #{recordStart},#{recordEnd};

SELECT count(*) from (
 SELECT count(*) as buyNum,sharesName ,sharesCode, sum(ratio) as totalRatio FROM shares
LEFT JOIN fund ON shares.fundId=fund.id

         AND   oneMonth>=#{oneMonth}
         AND threeMonth>=#{threeMonth}
         AND sixMonth>=#{sixMonth}
         AND oneYear>=#{oneYear}
         AND threeYear>=#{threeYear}
         AND always>=#{always}

GROUP BY shares.sharesName,sharesCode
 ) as temp;

SELECT count(*) as buyNum,sharesName,sharesCode , sum(ratio) as totalRatio FROM shares
LEFT JOIN fund ON shares.fundId=fund.id

         AND   oneMonth>=#{oneMonth}
         AND threeMonth>=#{threeMonth}
         AND sixMonth>=#{sixMonth}
         AND oneYear>=#{oneYear}
         AND threeYear>=#{threeYear}
         AND always>=#{always}

GROUP BY shares.sharesName ,sharesCode
ORDER BY buyNum DESC
